import axios from 'axios'
import React, { useState } from 'react'

import { Link, useParams } from 'react-router-dom'
import { useForm } from "react-hook-form"

const SingleUser = () => {
  const [users, setUsers] = useState({
    name: "",
    email: "",
    password: ""
  })
  const { id } = useParams()
  const {
    register,
    handleSubmit,

    formState: { errors },
  } = useForm()
  const onSubmit = async (data) => {
    const addUser = {
      firstname: data.firstname,
      lastname: data.lastname,
      email: data.email,

    }
    await axios.post(`http://localhost:8000/user/singleuser/${id}`, addUser).then((res) => {
      console.log(res)
      setUsers(res.data)
    }).catch((err) => {
      if (err.response) {
        console.log(err);
        alert("Error" + err)
      }
    })

  }
  return (
    <div className='add-user'>
      <Link to="/">Back</Link>
      <h3>Update User</h3>
      <form className='add-user-form' onSubmit={handleSubmit(onSubmit)}>
        <div className="inputGroup">
          <label htmlFor="firstname">first Name</label>
          <input type="text" name="firstname" value={users.firstname}
            placeholder='Enter First Name'
            {...register("firstname", { required: true })} />
          {errors.firstname && <span>This field is required</span>}
        </div>
        <div className="inputGroup">
          <label htmlFor="lastname">Last Name</label>
          <input type="text" name="lastname" value={users.lastname}
            placeholder='Enter Last Name'
            {...register("lastname", { required: true })} />
          {errors.lastname && <span>This field is required</span>}
        </div>
        <div className="inputGroup">
          <label htmlFor="email">Email</label>
          <input type="text" name="email" value={users.email}
            placeholder='Enter Your Email'
            {...register("email", { required: true })} />
          {errors.email && <span>This field is required</span>}
        </div>
        <div className="inputGroup">
          <button type='submit'> Update User</button>
        </div>
      </form>

    </div>
  )
}

export default SingleUser
