import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import { useForm } from "react-hook-form"
import toast from 'react-hot-toast'

const SingleUser = () => {
  const [users, setUsers] = useState([])
  const { id } = useParams()
  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get(`http://localhost:8000/user/singleuser/${id}`)
      console.log(res)
      setUsers(res.data)
    }
    fetchUser()
  }, [id])
  const {
    register,
    handleSubmit,
  } = useForm()
  const onSubmit = async (data) => {
    const addUser = {
      firstname: data.firstname,
      lastname: data.lastname,
      email: data.email,
      password: data.password
    }
    await axios.put(`http://localhost:8000/user/update/${id}`, addUser).then((res) => {
      console.log(res)
      if (res.data) {
        toast.success("user updated successfully")
      }
    }).catch((err) => {
      if (err.response) {
        console.log(err);
        alert("Error" + err)
      }
    })
  }
  return (
    <div className='add-user'>
      <Link to="/">Back</Link>
      <h3>Add User</h3>
      <form className='add-user-form' onSubmit={handleSubmit(onSubmit)} >
        <div className="inputGroup">
          <label htmlFor="firstname">first Name</label>
          <input type="text" name="firstname" value={users.firstname}
            {...register("firstname", { required: true })}
            placeholder='Enter First Name'
          />

        </div>
        <div className="inputGroup">
          <label htmlFor="lastname">Last Name</label>
          <input type="text" name="lastname" value={users.lastname}
            {...register("lastname", { required: true })}
            placeholder='Enter Last Name'
          />

        </div>
        <div className="inputGroup">
          <label htmlFor="email">Email</label>
          <input type="text" name="email" value={users.email}
            {...register("email", { required: true })}
            placeholder='Enter Your Email'
          />
        </div>
        <div className="inputGroup">
          <button type='submit'> Update User</button>
        </div>
      </form>

    </div>
  )
}

export default SingleUser
